INSERT INTO games (id, name, summary, release) VALUES (19575, 'Farpoint', 'Farpoint is an unnerving VR space adventure set on a hostile alien planet. On a mission to pick up scientists studying an anomaly near Jupiter, a rupture from the anomaly transports the player and scientists to an unknown alien world.', null) ON CONFLICT DO NOTHING;
