INSERT INTO games (id, name, summary, release) VALUES (14454, 'Fast Food', 'Dizzy''s back! In Fast Food, Dizzy must run around mazes scoffing loadsa food - chickens, burgers, pizzas, milkshakes but there''s a complication...look out for the monsters.', DATE('1991-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18287, 'Fast Food', 'The objective of the game is to eat as much fast food as you can by devouring the junk food that is approaching your huge mouth from the left of the screen. While doing that you should be careful to avoid the purple pickles - after having eaten six of those it is game over.
INSERT INTO games (id, name, summary, release) VALUES (8912, 'Fast Food Tycoon', 'Fast Food Tycoon was developed by Software 2000 as a economic simulation game. The player controls a pizza chain syndicate, and is able to manage many of the different aspects of running a pizza chain. The game spawned a sequel, and is generally regarded as an average-quality eco-sim.', DATE('2000-11-19')) ON CONFLICT DO NOTHING;
