INSERT INTO games (id, name, summary, release) VALUES (12589, 'Fighter Bomber', 'In the game, the player participates in the annual Strategic Air Command Bombing and Navigation Competition at Ellsworth Air Force Base in South Dakota. Aircrews from around the world come here to compete against each other, undertaking three missions which are monitored and points are awarded. The best aircrew then receives the Curtis E. LeMay Trophy.
