INSERT INTO games (id, name, summary, release) VALUES (414, 'Final Fantasy Tactics Advance', 'Squaresoft brings its popular Final Fantasy franchise to the Game Boy Advance in the form of strategic warfare. Final Fantasy Tactics Advance trails the story of a young boy named Marche who is magically transported from his sleepy modern-time home to a strange kingdom where magic and adventure reign supreme.INSERT INTO games (id, name, summary, release) VALUES (394, 'Final Fantasy Tactics: War of the Lions', null, DATE('2007-05-07')) ON CONFLICT DO NOTHING;
