INSERT INTO games (id, name, summary, release) VALUES (25744, 'Fishing Paradise 3D', 'Embark on a global fishing adventure with your guide "The Captain Fish Hook" in the ultimate cross platform MMO social Fishing game. Play for FREE on iPhone, iPad, Android and Facebook. Fish popular locations around the world, Catch, breed &amp; sell rare species, HUGE variety of fishing tackle and species, Visit friends &amp; fish their lakes, even hold your very own competitions, Multiplayer tournaments and leaderboards, Achievements, Collections, Unique Gameplay, Stunning 3D graphics and more...', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (12731, 'Fishing Planet', 'Fishing Planet is a FREE to play unique and highly realistic online first-person multiplayer fishing simulator developed for anglers to bring you the full thrill of actual fishing! Choose your lures, make your trophy catches, discover new possibilities and sharpen your real angling skills anywhere, anytime', null) ON CONFLICT DO NOTHING;
