INSERT INTO games (id, name, summary, release) VALUES (17357, 'Flashout 2', 'Flashout 2 offers a fresh and addicting racing experience featuring a redesigned and enhanced Career Mode with story elements, massive improvements in matter of steering, physics, opponent’s AI, and other gameplay elements, an Online Multiplayer Mode and introduces completely new and exciting game modes, such as Destruction, Elimination and Versus!', DATE('2014-05-04')) ON CONFLICT DO NOTHING;
