INSERT INTO games (id, name, summary, release) VALUES (19957, 'Flint and Charlie', 'Take the role of Charlie, a troubled teenager who''s getting some grown up life experience by working with his eccentric scientist uncle, Professor Flint. The two of you go away on a dubious scavengehunt to raid an abandoned deep space research station. Things do not go as planned and soon you find yourself in quite the pickle, of galactic proportions!', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (20330, 'Flinthook', 'Flinthook is a fast action-platformer with “roguelike” elements. Become space’s greatest pirate with your hookshot, pistol and slowmo powers!', DATE('2017-03-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4857, 'Flip''s Twisted World', 'Flip’s Twisted World is a 3D puzzle-platformer that follows the adventures of Flip who is accidentally trapped inside a world where up and down are just a matter of perspective. By turning a magic cube, he is able to turn the entire world, opening up new paths and possibilities. Players must think “outside of the box” as they navigate the world that is literally in the palms of their hands.', DATE('2010-10-18')) ON CONFLICT DO NOTHING;
