INSERT INTO games (id, name, summary, release) VALUES (17649, 'Frayed Knights: The Skull of S''makh-Daon', 'Frayed Knights: The Skull of S''makh-Daon is a 3D indie computer RPG for Windows featuring turn-based combat, old-school sensibilities, and a story of high adventure and comedy. Who says RPGs have to be so serious?', DATE('2014-06-03')) ON CONFLICT DO NOTHING;
