INSERT INTO games (id, name, summary, release) VALUES (11186, 'Gears of War 4', 'A new saga begins for one of the most acclaimed video game franchises in history. After narrowly escaping an attack on their village, JD Fenix and his friends, Kait and Del, must rescue the ones they love and discover the source of a monstrous new enemy.', DATE('2016-10-10')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (1319, 'Gears of War: Judgment', '"Gears of War: Judgment" takes you back before events of the original "Gears of War" trilogy to the immediate aftermath of Emergence Day - the defining event of the "Gears of War" universe. Follow Kilo Squad, led by Damon Baird and Augustus "The Cole Train" Cole, as they fight to save the city of Halvo Bay from an unstoppable enemy in the most intense ''Gears'' game yet.', DATE('2013-03-18')) ON CONFLICT DO NOTHING;
