INSERT INTO games (id, name, summary, release) VALUES (19280, 'GeneRally', 'GeneRally is a freeware racing game originally developed by brothers Hannu and Jukka Räbinä from Finland. Although it features a 3D graphics engine, GeneRally has a top-down perspective, where you can see all the cars (which is perfect for multiplayer races). Its art design calls to mind older arcade racing games such as Super Sprint and Slicks ''N'' Slide. PCGamer, in an issue about free PC games, described it as "A minimalist Micro Machines".', DATE('2002-07-07')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8362, 'Geneforge', 'Geneforge is an Indie fantasy role-playing game, the first game in the five-part Geneforge Saga. It features an enormous world, the ability to create your own army of lethal, totally obedient creatures, and a cunning enemy AI with foes who can go on patrols, stalk you, and run for help.
INSERT INTO games (id, name, summary, release) VALUES (8363, 'Geneforge 2', 'Geneforge 2 is a fantasy role-playing epic with a science fiction twist. In Geneforge 2, you explore a huge world, choose which side you will fight for, and make your own horde of completely obedient mutant monsters.', DATE('2003-07-24')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8365, 'Geneforge 3', 'Geneforge 3 is the next, huge installment of the Geneforge saga. In this fantasy adventure, you can explore strange, hostile lands, choose which side you will fight for, and, as always, make your own horde of completely obedient mutant monsters.', DATE('2005-03-27')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8369, 'Geneforge 4: Rebellion', 'Geneforge 4: Rebellion is the fourth video game in the Geneforge series of role-playing video games created by Spiderweb Software.', DATE('2006-11-19')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8370, 'Geneforge 5: Overthrow', 'Geneforge 5: Overthrow is the fifth and final video game in the Geneforge series of role-playing video game created by Spiderweb Software.', DATE('2008-11-27')) ON CONFLICT DO NOTHING;
