INSERT INTO games (id, name, summary, release) VALUES (3098, 'Giana Sisters: Twisted Dreams', 'Giana Sisters: Twisted Dreams is a blistering hardcore platformer with contrasting worlds. Warp Giana’s vivid dreams to alter your abilities and destroy the twisted creatures lurking within. Giana Sisters: Twisted Dreams combines the challenge of retro era platforming with graphics that pack a visual punch and tight controls.
