INSERT INTO games (id, name, summary, release) VALUES (24684, 'Giant Machines 2017', 'Become a one man machine to operate the biggest equipment in the world. Use it to dig, drive, transport, demolish and more. Make your way in to space by fulfilling objectives in 12 exciting missions in immersive story mode.', DATE('2016-09-28')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (12648, 'Giant Shadow City', 'Giant Shadow City (tentative title) is an upcoming action survival game by Bandai Namco and Granzella. It will feature giant life forms that are said to be several dozen meters tall, huge flying objects, all things that are considered threats to mankind. This project will depict the extraordinary story of humans that will do their best to survive in this world of giant shadows.', null) ON CONFLICT DO NOTHING;
