INSERT INTO games (id, name, summary, release) VALUES (10363, 'Gigantic Army', 'From the makers of SATAZIUS: 6 levels of retro arcade-styled mechanized chaos and addictive game play in this heartfelt homage to 16-bit mech shooters such as Cybernator / Assault Suits Valken, Metal Warriors, and Front Mission: Gun Hazard!
INSERT INTO games (id, name, summary, release) VALUES (12431, 'Gilbert Goodmate and the Mushroom of Phungoria', 'A 2001 point-and-click adventure game, re-released on Steam on June 23, 2015.', DATE('2001-06-07')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (16677, 'Gimbal', 'Gimbal is the multiplayer shooter with buildable vehicles. Craft the perfect ship, and then take to the skies with other pilots in real time combat. Cap flags, race ships, and blow each other up in a social multiplayer environment. Gimbal is the shooter you''ve been waiting for.', DATE('2013-10-01')) ON CONFLICT DO NOTHING;
