INSERT INTO games (id, name, summary, release) VALUES (10364, 'Girlfriend Rescue', 'Girlfriend Rescue is a thrilling new adventure with an enthralling story where "Taken" meets "24". Innovative in many aspects, mixing rogue-style RPG and Beat ''em Up in a unique way, this game is a clear departure from the usual Fantasy formula.
INSERT INTO games (id, name, summary, release) VALUES (16841, 'Girls Like Robots', 'Their faces are the playing pieces.  The rules are their emotions!
INSERT INTO games (id, name, summary, release) VALUES (8384, 'Gish', 'Gish isn''t your average hero. In fact, he''s not your average anything...you see, Gish is a ball of tar. A Sunday stroll with his lady friend, Brea, goes awry when a shadowy figure emerges from an open manhole and pulls Brea below ground. Following Brea''s calls for help, Gish suddenly finds himself in the subterranean sewers of Dross, a long forgotten city filled with twisting corridors, evil traps, and some of the most demented creatures imaginable. 
INSERT INTO games (id, name, summary, release) VALUES (11338, 'Gitaroo Man', 'Gitaroo Man is a rhythm video game featuring visual production by Mitsuru Nakamura and an original soundtrack by Japanese band COIL.', DATE('2001-06-20')) ON CONFLICT DO NOTHING;
