INSERT INTO games (id, name, summary, release) VALUES (20379, 'Goblins & Grottos', 'A puzzle platformer with comic RPG elements, where you play as a goblin trying to escape from a team of greedy adventurers. Handcrafted levels with delightful pixel art and carefully programmed interactions: climb walls, hang from chains, push barrels onto the heads of your enemies, lure them into traps with the promise of gold coins.', DATE('2016-07-06')) ON CONFLICT DO NOTHING;
