INSERT INTO games (id, name, summary, release) VALUES (18909, 'Goscurry', 'Relax, don''t panic, be brave. Reach as many cities as you can by piloting a ship on a perpetually assembling road suspended over a stylized and surreal landscape, while taking sharp corners, evading hazards and rushing through speedfreak modes. A hard electro-beat game.', DATE('2015-01-14')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (7427, 'Gotcha', 'Gotcha is a two-player maze game where the objective is to catch the other player. A maze is displayed on the screen. The first player controls the Pursuer which is represented by a square and the second player controls the Pursued which is represented by a plus sign. As the Pursuer moves closer and closer to the Pursued, an electronic beep sound increases in frequency to a feverish pitch until the Pursuer catches the Pursued. Each time, the Pursuer catches the Pursued, a point is scored and the chase starts over again.', DATE('1973-12-30')) ON CONFLICT DO NOTHING;
